    private void txtNStateChanged(javax.swing.event.ChangeEvent evt) {//GEN-FIRST:event_txtNStateChanged
        
        int n = Integer.parseInt(txtN.getValue().toString());        
        int f = 1;
        int c = n;
        String calcFat = "";
        
        // Calcula Fatorial - ex: 5! = 5 x 4 x 3 x 2 x 1
        while (c >= 1) {
            f *= c;
            if (c > 1) {
                calcFat += c + " x ";
            } else {
                calcFat += c;
            }            
            c--;
        }
        lblNum.setText(Integer.toString(n) + "!");
        if (n == 1 || n == 0) {
            lblDetalhe.setText("= 1");
        } else {
            lblDetalhe.setText("= " + calcFat + " = " + f);
        }
    }//GEN-LAST:event_txtNStateChanged

